import React from "react";

export default function Practice(props) {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    comments: '',
    isFriendly: true
  });

  const handleChange = (event) => {
    const {name, value, type, checked} = event;
    setFormData(prev => ({
      ...prev, [name]: value
    }));
  }

  console.log(formData.isFriendly);
  
  return (
    <form>
        <input type="text" placeholder="First Name" id='firstName' name='firstName' onChange={ handleChange } value={ formData.firstName } style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <input type="text" placeholder="Last Name" id='lastName' name='lastName' onChange={ handleChange } value={ formData.lastName} style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <input type="email" placeholder="Email" id='email' name='email' onChange={ handleChange } value={ formData.email } style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <textarea name='comments' placeholder="Comments" value={ formData.comments } onChange={ handleChange } style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <input type="checkbox" id='isFriendly' checked={formData.isFriendly} name='isFriendly' onChange={ handleChange } />
        <label htmlFor="isFriendly">Are you friendly?</label>
    </form>
  );
}
