import Die from "./Die";
import { useState, useEffect } from "react";
import { nanoid } from 'nanoid';

export default function Main() {

  const generateNewDie = () => {
    return {
      id: nanoid(),
      value: Math.floor(Math.random() * 6) + 1,
      isHeld: false
    }
  };

  const allNewDice = () => {
    const arr = [];
    for (let i = 0; i < 10; i++) {
        arr.push(generateNewDie());
    }

    return arr;
  }

  const toggleHeld = (id) => {
    setDice(prevDice => {
      return prevDice.map(die => {
        return die.id === id ? 
        {...die, isHeld: !die.isHeld}: die
      })
    });
  };

  const [dice, setDice] = useState(allNewDice());
  const [tenzies, setTenzies] = useState(false);

  const dieComponents = dice.map(die => 
    <Die
     key={die.id} 
     keys={{...die}}
     toggleHeld={() => toggleHeld(die.id)}
    />
    );

  const rollDice = () => {
   setDice(prevDice => {
    return prevDice.map(die => {
      return !die.isHeld ? 
      generateNewDie(): die
    });
   });
  };

  useEffect(() => {

    return () => {
      setTenzies(true);
    }
  }, [dice]);

  return (
    <main
      className="main"
      style={{
        width: '60vh',
        height: '60vh',
        marginTop: '50px',
        padding: '20px',
        borderRadius: '10px',
        backgroundColor:'#F5F5F5',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-evenly',
        alignItems: 'center'
      }}
    >
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center'
        }}
      >
          <h1
            style={{
              marginBottom: 0
            }}
          >Tenzies
          </h1>
          <p
            style={{
              marginTop: 0,
              textAlign: 'center'
            }}
          >Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
        </div>
        <div
            style={{
                display: "grid",
                gridTemplate: '50px 50px / 50px 50px 50px 50px 50px',
                gap: '20px'
            }}
        >
            {dieComponents}
        </div>
        <button
            className="button"
            type="button"
            style={{
                marginTop: '50px',
                color: 'white',
                background: '#5035FF',
                width: '100px',
                border: 'none',
                height: '50px',
                fontSize: '1.2rem',
                borderRadius: '6px'
            }}
            onClick={rollDice}
        >
            { tenzies ? 'New Game': 'Roll'}
        </button>
    </main>
  );
};
