export default function Cards(props) {
    let badgeText;
    if (props.openSpots === 0) {
      badgeText = 'SOLD OUT'
    } else if (props.location === 'Online') {
      badgeText = 'ONLINE'
    }
    return (
      <div className='card'>
        <img src ={ props.covering } alt='card'/>
        <p><span className='star-logo'>⭐</span> { props.stats.rating }({ props.stats.reviewCount }) • { props.location }</p>
        <p>From ${ props.price } /person</p>
      </div>
    );
}
