export default function Die(props) {
  console.log(props.isHeld);
    return (
      <div
        className="die"
        style={{
            height: '50px',
            width: '50px',
            backgroundColor: 'white',
            borderRadius: '5px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            boxShadow: '0px 2px 2px rgba(0, 0, 0, 0.15)',
            marginTop: 'auto',
            marginBottom: 'auto',
            backgroundColor: props.isHeld ? 'light-green': ''
        }}
        onClick={() => props.toggleHeld(props.id)}
      >
        <h2>{props.value}</h2>
      </div>
    );
};
