export default function NavBar(props) {

  const toggleMode = () => {
    console(props.background);
  }  
  return (
    <header 
        className="header"
        style={{
            borderBottom: props.background.lightMode === true ?
            '3px solid grey': 'none'
        }}
    >
        <img className="logo" alt="react logo"/>
        <h2 className="logo-text">ReactFacts</h2>
        <div className="button-outer-container">
            <p className="lightmode-text">light</p>
            <div 
                className="button-inner-container"
                style={{
                    background: props.background.lightMode === true ?
                    'black': 'white'
                }}
            >
                <button
                    className="mode-button">
                    onClick={toggleMode}
                    style={{
                        marginLeft: props.background.lightMode === true ?
                        'auto': 0
                    }}
                </button>
            </div>
            <p className="darkmode-text">dark</p>
        </div>
    </header>
  );
};
