import { boxes } from "./Boxes";
import Box from "./Box";
import React from 'react';

export default function Practice() {
  const [arrayOfBoxes, setArrayOfBoxes] = React.useState(boxes);
  const toggle = (id) => {
    setArrayOfBoxes(prev => {
      return prev.map(instance => {
        return instance.id === id ? { ...instance, on: !instance.on }: instance;
      });
    });
  };
  const jsxBoxes = arrayOfBoxes.map(box => <Box key={ box.id } on={ box.on } id={box.id}  toggle={ () => toggle(box.id) }/>);

  return (
    <div>
        { jsxBoxes }
    </div>
  );
};
