import React from "react";

export default function Practice(props) {
  const [firstName, setFirstName] = React.useState('');

  const removeOutline = ($) => $.target.style.outline = 'none';

  const handleChange = ($) => {
    $.preventDefault();
    setFirstName($.target.value);
  };

  console.log(firstName);
 
  return (
    <form>
        <input type="text" placeholder="First Name" id='firstName' onFocus={ removeOutline } onChange={ handleChange }>
        </input>
    </form>
  );
}
