import React from 'react';

export default function Practice() {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    comments: '',
    isFriendly: true,
    employment: ''
  });

  const handleChange = (event) => {
    const {name, value, type, checked} = event.target;
    setFormData(prevData => ({
      ...prevData, [name]: type === 'checkbox' ? checked: value
    }));
  };

  const handleOutline = (event) => {
    event.target.style.outlineColor = 'skyblue';
  };

  return (
    <form>
        <input 
        type='text' name='firstName' id='firstName' placeholder='First name'
        value={formData.firstName}
        onChange={handleChange}
        onClick={handleOutline}
        />
        <br />
        <br />
        <input 
        type='text' name='lastName' id='lastName' placeholder='Last name'
        value={formData.lastName}
        onChange={handleChange} 
        onClick={handleOutline}
        />
        <br />
        <br />
        <input 
        type='email' name='email' id='email' placeholder='email'
        value={formData.email}
        onChange={handleChange}
        onClick={handleOutline}
        />
        <br />
        <br />
        <textarea 
        name='comments' id='comments' placeholder='Comments'
        value={formData.comments}
        onChange={handleChange}
        onClick={handleOutline}
        />
        <br />
        <br />
        <input 
        type='checkbox'
        name='isFriendly'
        id='isFriendly'
        checked={formData.isFriendly}
        onChange={handleChange}
        />
        <label htmlFor='isFriendly'>Are you friendly?</label>
        <br />
        <br />
        <fieldset>
            <legend>Current employment status</legend>
                <input
                type='radio'
                name='employment'
                id='unemployed'
                value='unemployed'
                checked={formData.employment === 'unemployed'}
                onChange={handleChange}
                />
                <label htmlFor='unemployed'>Unemployed</label>
                <br />
                <input
                type='radio'
                name='employment'
                id='part-time'
                value='part-time'
                checked={formData.employment === 'part- time'}
                onChange={handleChange}
                />
                <label htmlFor='part-time'>Part time</label>
                <br />
                <input
                type='radio'
                name='employment'
                id='full-time'
                value='full-time'
                checked={ formData.employment === 'full- time'}
                onChange={handleChange}
                />
                <label htmlFor='full-time'>Full time</label>

        </fieldset>
    </form>
  );
};
