import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

export default function CarDetail() {
  const param = useParams();
  const arr = [];
  const car = JSON.parse(localStorage.getItem('cars'));
  arr.push(car);
  const fresh = null;
  for (let i in arr) {
    console.log(arr[i].id);
    if (i.id === param.id) {
      fresh = i;
    }
  }
  console.log(arr);
  console.log(fresh);
  return (
    car ? (
        <div className="car-details-container">
       <img src={ car.imageUrl } alt="car avatar" />
       <div 
             className="car-details-type-container"
             style={{
              background: car.type === 'flashy' ?
              'orange': car.type === 'top class' ?
              'black': car.type === 'classic' ?
              'indigo': 'cadetblue'
             }}
          >
            <p className="car-details-type">{ car.type }</p>
        </div>
          <h1>{ car.name }</h1>
          <span className="price">${ car.price }/day</span>
        <p className="car-description">{ car.description }</p>
        <button className="car-rent-button">Rent this car</button>
    </div>
    ) : <p>Loading...</p>
  );
};
