import { useParams } from "react-router-dom";

export default function CarDetail() {
  const param = useParams();
  const arr = [];
  const cars = JSON.parse(localStorage.getItem('cars'));
  arr.push(cars);
  let car = null;
  for (let i of arr) {
    for (let j of i) {
      if (j.id === param.id) {
        car = j
      }
    }
  }

  return (
      <div className="car-details-container">
       <img src={ car.imageUrl } alt="car avatar" />
       <div 
             className="car-details-type-container"
             style={{
              background: car.type === 'flashy' ?
              'orange': car.type === 'top class' ?
              'black': car.type === 'classic' ?
              'indigo': 'cadetblue'
             }}
          >
            <p className="car-details-type">{ car.type }</p>
        </div>
          <h1>{ car.name }</h1>
          <span className="car-detail-sprice">${ car.price }/day</span>
        <p className="car-description">{ car.description }</p>
        <button className="car-rent-button">Rent this car</button>
    </div>
  );
};
