import Home from "./Home";
import About from "./About";
import {  BrowserRouter, Routes, Route } from 'react-router-dom';
import { createServer, Model } from 'miragejs';

createServer({
  models: {
    vans: Model,
  },

  seeds(server) {
    server.create("vans", {
        id: "1",
        name: "Modest Explorer",
        price: 80,
        description: "Modest Explorer is a van to get you out of the house and is well equipped with solar panels, a composting toilet and all other things",
        imageUrl: '../public/images/audi-on-water.jpg'
    })
    server.create("vans", {
        id: "2",
        name: "Beach Bun",
        price: 60,
        description: "Inspired by surfers and travellers, it was created to be fairly simple but with cool features in it that you won't find in any other vehicle",
        imageUrl: '../public/images/waterfall.jpg'
    })
    server.create("vans", {
        id: "3",
        name: "Reliable Red",
        price: 90,
        description: "Reliable Red is a van that was made for travelling. The inside is comfortable and cozy with plenty of space to stretch out in.",
        imageUrl: '../public/images/tall-building.jpg'
    })
    server.create("vans", {
        id: "4",
        name: "Dream Finder",
        price: 50,
        description: "Dream finder is the perfect van to travel in an experience. with ceiling height of 2.1m you can stand up in this van because there is great room",
        imageUrl: '../public/images/superb-vase.jpg'
    })
    server.create("vans", {
        id: "5",
        name: "Green Wonder",
        price: 80,
        description: "A superb van that can help with taking your travelling life to the next level",
        imageUrl: '../public/images/sky-scrapers.jpg'
        })
    },

    routes() {
        this.namespace = "api"

        this.get('/vans', (schema, request) => {
            return schema.vans.all()
        })

        this.get('/vans/:id', (schema, request) => {
            const id = request.params.id
            return schema.vans.find(id)
        })
    }
});

export default function App() {
  return (
    <BrowserRouter>
       <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
       </Routes>
    </BrowserRouter>
  );
}
