import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

export default function HostCarsDetails() {
    const id = useParams();
    const [car, setCar] = useState(null);
    useEffect(() => {
      const getCar = async () => {
         try {
            const res = await fetch(`/api/host/cars/${id}`);
            const data = await res.json();
            setCar(data.cars);
         } catch(err) {
           if (err) {
            throw new Error("Something went wrong...");
           }
         }
      };
      getCar();
    }, [id]);
    console.log(car);
    return (
      car ? (
        <>
            <Link to="..">Back to all cars</Link>
            <div className="host-car-details-container">
            <div className="host-car-photo-container">
              <img src={ car.imageUrl } alt="car avatar" />
              <div className="host-car-details-container">
                <div className="host-car-type">
                  { car.type }
                </div>
                <h1 className="host-car-name">
                  { car.name }
                </h1>
                <p className="host-car-price">
                  <strong>${ car.price }</strong>/day
                </p>
                </div>
              </div>
            </div>
          </>
      ) : <p>Loading...</p>
    );
  };
