import { useState, useEffect } from "react";
import { Link } from 'react-router-dom';

export default function Cars() {
  const [cars, setCars] = useState([]);
  useEffect(() => {
    const getCars = async () => {
      const res = await fetch('/api/cars');
      const data = await res.json();
      setCars(data.cars);
    };
    getCars();
  }, []);

  localStorage.setItem('cars', JSON.stringify(cars));

  const carsFromStorage = JSON.parse(localStorage.getItem('cars'));
  console.log(carsFromStorage);
  const [component, setComponent] = useState(null);
  const carComponent = carsFromStorage.map(car => {
    return (
      <Link key={ car.id } className="car-link" to={`/cars/${ car.id }`}>
        <div className="car-image-container">
          <img src={ car.imageUrl } alt="car avatar" />
          <h3>{ car.name }</h3>
          <span className="price">${ car.price }/day</span>

          <div 
             className="type"
             style={{
              background: car.type === 'flashy' ?
              'orange': car.type === 'top class' ?
              'black': car.type === 'classic' ?
              'indigo': 'cadetblue'
             }}
          >
            <p>{ car.type }</p>
          </div>
        </div>
      </Link>
    );
  });
  setComponent(carComponent);

  return (
        component ? (<div>
        <header>
          <h1 className="cars-h1">Explore our car options</h1>
        </header>
        <main className="cars-main">
          { component }
        </main>
      </div>): <p>Loading....</p>
  );
};
