import { useState, useEffect } from "react";
import { Link } from 'react-router-dom';

export default function Cars() {
  useEffect(() => {
    const getCars = async () => {
      try {
        const res = await fetch('/api/cars');
        const data = await res.json();
        localStorage.setItem('cars', JSON.stringify(data.cars));
      } catch(err) {
        throw new Error('Something went wrong');
      }
    }
    getCars();
  }, []);

  const carComponent = cars.map(car => {
    return (
      <Link key={ car.id } className="car-link" to={`/cars/${ car.id }`}>
        <div className="car-image-container">
          <img src={ car.imageUrl } alt="car avatar" />
        </div>
        <h3>{ car.name }</h3>
        <p className="price">${ car.price }/day</p>
        <div 
             className="type-container"
             style={{
              background: car.type === 'flashy' ?
              'orange': car.type === 'top class' ?
              'black': car.type === 'classic' ?
              'indigo': 'cadetblue'
             }}
          >
            <p className="type">{ car.type }</p>
          </div>
      </Link>
    );
  });

  return (
      <div>
      <header>
        <h1 className="cars-h1">Explore our car options</h1>
      </header>
      <main className="cars-main">
        { carComponent }
      </main>
    </div>
  );
};
