import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

export default function CarDetail() {
  const param = useParams();
  const [car, setCar] = useState(null);
  useEffect(() => {
    const getCar = async () => {
      try {
        const res = await fetch(`/api/cars/${ param.id }`);
        const data = await res.json();
        setCar(data.cars);
      } catch(err) {
        throw new Error('Something went wrong');
      }
    };
    getCar();
  }, []);
  
  console.log(car)
  return (
    car ? (
        <div className="car-details-container">
       <img src={ car.imageUrl } alt="car avatar" />
          <h1>{ car.name }</h1>
          <span className="price">${ car.price }/day</span>
          <div 
             className="type"
             style={{
              background: car.type === 'flashy' ?
              'orange': car.type === 'top class' ?
              'black': car.type === 'classic' ?
              'indigo': 'cadetblue'
             }}
          >
            <p className="type">{ car.type }</p>
        </div>
        <p>{ car.description }</p>
    </div>
    ) : <p>Loading...</p>
  );
};
