export default function NavBar(props) {
const body = document.querySelector('body');
body.style.backgroundColor = props.background.lightMode ? 'white': 'rgba(0, 0, 0, 0.963)';
  const toggleMode = () => {
    props.setBackground(background => {
        return background.lightMode === true ?
        { 
            ...background,
             lightMode: false,
             darkMode: true
         }
        :
        { 
            ...background, 
            lightMode: true,
            darkMode: false
        }
    });
  };

  return (
    <header 
        className="header"
        style={{
            borderBottom: props.background.lightMode === true ?
            '3px solid whitesmoke': 'none',
            background: props.background.darkMode ? 'black': 'white',
        }}
    >
        <img src='./images/react-logo.png' className="logo" alt="react logo"/>
        <h2 className="logo-text">ReactFacts</h2>
        <div className="button-outer-container">
            <p 
                className="lightmode-text"
                style={{
                    color: props.background.lightMode === true ? 'black': 'white'
                }}
            >
                light
            </p>
            <div 
                className="button-inner-container"
                style={{
                    background: props.background.lightMode === true ?
                    'black': 'white'
                }}
            >
                <button
                    className="mode-button"
                    onClick={toggleMode}
                    style={{
                        marginLeft: props.background.lightMode === true ?
                        0: 'auto',
                        height: '20px',
                        width: '20px',
                        display: 'block',
                        background: props.background.lightMode === true ? 'white': 'black',
                        
                    }}
                >
                </button>
            </div>
            <p 
                className="darkmode-text"
                style={{
                    color: props.background.lightMode === true ? 'black': 'white'
                }}
            >
                dark
            </p>
        </div>
    </header>
  );
};
