import Die from "./Die";
import { useState } from "react";
import { nanoid } from 'nanoid';

export default function Main() {

  const allNewDice = () => {
    const arr = [];
    for (let i = 0; i < 10; i++) {
        arr.push({
            id: nanoid(),
            value: Math.floor(Math.random() * 6) + 1,
            isHeld: false
        });
    }

    return arr;
  }

  const toggleHeld = (id) => {
    setDice(prevDice => {
      return prevDice.map(die => {
        return die.id === id ? 
        {...die, isHeld: !die.isHeld}: die
      })
    });
  };

  const [dice, setDice] = useState(allNewDice());
  const dieComponents = dice.map(die => 
    <Die
     key={die.id} 
     keys={{...die}}
     toggleHeld={() => toggleHeld(die.id)}
    />
    );

  const rollDice = () => {
   setDice(prevDice => {
    return prevDice.map(die => {
      const randomNumber = Math.floor(Math.random() * 6) + 1;
      return !die.isHeld ? 
      {...die, value: randomNumber} : die
    });
   });
  };


  return (
    <main
      className="main"
      style={{
        width: '80vh',
        height: '80vh',
        marginTop: '50px',
        borderRadius: '10px',
        backgroundColor:'#F5F5F5',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'
      }}
    >
        <div
            style={{
                display: "grid",
                gridTemplate: '50px 50px / 50px 50px 50px 50px 50px',
                gap: '20px'
            }}
        >
            {dieComponents}
        </div>
        <button
            className="button"
            type="button"
            style={{
                marginTop: '50px',
                color: 'white',
                background: '#5035FF',
                width: '100px',
                border: 'none',
                height: '50px',
                fontSize: '1.2rem',
                borderRadius: '6px'
            }}
            onClick={rollDice}
        >
            Roll
        </button>
    </main>
  );
};
