import { boxes } from "./Boxes";
import Box from "./Box";
import React from 'react';

export default function Practice() {
  const [arrayOfBoxes, setArrayOfBoxes] = React.useState(boxes);
  const toggle = (id) => {
    const box = arrayOfBoxes.find(box => box.id === id);
    setArrayOfBoxes(prev => {
      return prev.map(instance => {
        if (instance.id === box.id) {
          instance.on = !instance.on;  
        }

        return instance;
      });
    });
  };
  const jsxBoxes = arrayOfBoxes.map(box => <Box key={ box.id } on={ box.on } id={box.id}  handleClick={ toggle }/>);

  return (
    <div>
        { jsxBoxes }
    </div>
  );
};
