import React from "react";
import { memeApiObject } from "./memeData";


export default function Meme() {

  const [image, setImage] = React.useState({
    topText: '',
    bottomText: '',
    randomImage: './images/tall-building.jpg'
  });

  const [allMemeImages, setAllMemeImages] = React.useState(memeApiObject);

  function addImage() {
    const arrayOfMemeObjects = allMemeImages.data.memes.map(meme => meme);
    const size = arrayOfMemeObjects.length;
    const url =  arrayOfMemeObjects[Math.floor(Math.random() * size)].url;
    setImage(prev => {
      return { ...prev, randomImage: url }
    });
    console.log(image.randomImage);
  };

  const handleChange = (event) => {
    const {name, value} = event.target;
    setImage(prevData => ({
      ...prevData, 
    }));
  };

  return (
    <main>
      <div className="form">
        <input
        type="text" 
        className='first-input' 
        placeholder='Shut up'
        name="topText"
        />
        <input 
        type="text" 
        className='second-input' 
        placeholder='and take my money'
        name="bottomText"
        />
        <button onClick={ addImage }>
          <p>Get a new meme image</p>
          <span>ðŸ’¼</span>
        </button>
        <img src={ image.randomImage } alt='react' />
      </div>
    </main>
  );
};
