import React from "react";
import Count from "./Count";

export default function Practice() {
  const [count, setCount] = React.useState(0);

  const add = () => setCount(prev => prev + 1);
  const subtract = () => count >= 1 ? setCount(prev => prev - 1) : count;
  return (
    <div style={{ display: 'flex', position: 'relative', justifyContent: 'center', alignItems: 'center', marginTop: '40px' }}>
      <button style={{ background: 'grey', borderRadius: '100px', height: '50px', width: '50px', marginRight: '90px', marginTop: '50px', position: 'absolute', color: 'white', border: 'none', fontSize: '35px' }} onClick={ subtract }>-</button>
      <Count number={ count }/>
      <button style={{ background: 'grey', borderRadius: '100px', height: '50px', width: '50px', marginLeft: '100px', marginTop: '50px', position: 'absolute', border: 'none', color: 'white', fontSize: '15px' }} onClick={ add }>+</button>
    </div>
  );
};
