import React from "react";
export default function Practice() {

    const [things, setThings] = React.useState(['Thing 1', 'Thing 2']);

    const addItem = () => setThings(prev => [...prev, `Thing ${things.length + 1 }`]);
    const thingsArray = things.map(thing => <p key={thing}>{ thing }</p>);

    return (
      <div>
        <button style={{ color: 'white', width: '200px', height: '80px', borderRadius: '50px', display: 'flex', justifyContent: 'center', alignItems: 'center', marginTop: '20px', border: '1px solid white', background: 'none'}} onClick={ addItem } onMouseOver={() => this.style.background = 'white'}>
            <h2>Add Item</h2>
        </button>
        { thingsArray }
    </div>
    );
};
