import React from "react";

export default function Practice(props) {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    comments: '',
    isFriendly: true,
    employment: ""
  });

  const handleChange = (event) => {
    const {name, value, type, checked} = event;
    setFormData(prev => ({
      ...prev, [name]: type === "checkbox" ? checked : value
    }));
  }

  console.log(formData.isFriendly);
  
  return (
    <form>
        <input type="text" placeholder="First Name" id='firstName' name='firstName' onChange={ handleChange } value={ formData.firstName } style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <input type="text" placeholder="Last Name" id='lastName' name='lastName' onChange={ handleChange } value={ formData.lastName} style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <input type="email" placeholder="Email" id='email' name='email' onChange={ handleChange } value={ formData.email } style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <textarea name='comments' placeholder="Comments" value={ formData.comments } onChange={ handleChange } style={{ padding: 7, borderRadius: 5}}/>
        <br />
        <br />
        <input type="checkbox" id="isFriendly" name="isFriendly" onChange={ handleChange } checked={ formData.isFriendly }/>
        <label htmlFor="isFriendly">Are you friendly?</label>
        <fieldset>
            <legend>
              Current employment status
            </legend>
            <input type="radio" id='unemployed' name='employment' value='unemployed' checked={formData.employment === 'unemployed'}/>
            <label htmlFor="unemployed">Unemployed</label>
            <br />
            <input type="radio" id='part-time' name='employment' value='part-time' checked={formData.employment === 'part-time'} onChange={ handleChange }/>
            <label htmlFor="part-time">Part-time</label>
            <br />
            <input type="radio" id='full-time' name='employment' value='full-time' onChange={ handleChange } checked={ formData.employment === 'full-time'}/>
            <label htmlFor="full-time" >Full-time</label>
            <br />
        </fieldset>
    </form>
  );
}
