import { boxes } from "./Boxes";
import Box from "./Box";
import React from 'react';

export default function Practice() {
  const [arrayOfBoxes, setArrayOfBoxes] = React.useState(boxes);
  const change = () => {
    setArrayOfBoxes(prev => ([{ ...prev, on: !prev.on }]));
    console.log()
  };
  const jsxBoxes = arrayOfBoxes.map(box => <Box key={ box.id } on={ box.on } handleClick={ change }/>);

  return (
    <div>
        { jsxBoxes }
    </div>
  );
};
