import React from 'react';

export default function Practice() {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    comment: ''
  });

  const handleChange = (event) => {
    const [name, value] = event.target;
    setFormData(prevData => ({
      ...prevData, [name]: value
    }));
  };

  const handleOutline = (event) => {
    event.target.style.outlineColor = 'skyblue';
  };

  return (
    <form>
        <input type='text' name='firstName' id='firstname' placeholder='First name' onClick={ handleOutline }/>
        <br />
        <br />
        <input type='text' name='lastName' id='lastname' placeholder='Last name' onClick={ handleOutline }/>
        <br />
        <br />
        <input type='email' name='email' id='email' placeholder='email' onClick={ handleOutline }/>
    </form>
  );
};
