import React, { useState, useEffect }from "react";


export default function Meme() {

  const [image, setImage] = useState({
    topText: '',
    bottomText: '',
    randomImage: './images/tall-building.jpg'
  });

  const [allMemeImages, setAllMemeImages] = useState([]);

  useEffect(() => {
    fetch('https://api.imgflip.com/get_memes')
      .then(res => res.json)
      .then(memeObj => setAllMemeImages(memeObj.data));
  }, []);

  function addImage() {
    const arrayOfMemeObjects = allMemeImages.memes.map(meme => meme);
    const size = arrayOfMemeObjects.length;
    const url =  arrayOfMemeObjects[Math.floor(Math.random() * size)].url;
    setImage(prev => {
      return { ...prev, randomImage: url }
    });
  };

  const handleChange = (event) => {
    const {name, value} = event.target;
    setImage(prevData => ({
      ...prevData, [name]: value
    }));
  };

  console.log(image);

  return (
    <main>
      <div className="form">
        <input
        type="text" 
        className='first-input' 
        placeholder='Shut up'
        name="topText"
        value={image.topText}
        onChange={handleChange}
        />
        <input 
        type="text" 
        className='second-input' 
        placeholder='and take my money'
        name="bottomText"
        value={image.bottomText}
        onChange={handleChange}
        />
        <button onClick={ addImage }>
          <p>Get a new meme image</p>
          <span>ðŸ’¼</span>
        </button>
      </div>
      <div className="meme">
            <img src={ image.randomImage } alt='react' />
            <h2 className="top">{image.topText}</h2>
            <h2 className="bottom">{image.bottomText}</h2>
        </div>
    </main>
  );
};
