import React from "react";
import Star from './Star';
export default function Practice() {
  const [contact, setContact] = React.useState({
    firstName: 'John',
    lastName: 'Doe',
    phone: '+1 (719) 555-1212',
    email: 'itsmyrealname@example.com',
    isFavourite: false
  });

  const starIcon = contact.isFavourite ? '‚≠ê': '‚ú°Ô∏è';
  const toggleFavorite = () => setContact(prev => ({ ...prev, isFavourite: !prev.isFavourite }));

  return (
    <div style={{ border: '3px solid whitesmoke', display: 'flex', flexDirection: 'column', justifyContent: 'center', boxShadow: '2px 2px 2px grey', borderRadius: '7px', marginTop: '30px', paddingBottom: '20px', width: '100px'}}>
      <div style={{ fontSize: 150 }}>üë®‚Äçüíº</div>
      <Star isFilled={ contact.isFavourite } handleClick={ toggleFavorite } />
      <h1 style={{ marginLeft: '10px' }}>{ `${contact.firstName} ${contact.lastName}` }</h1>
      <small style={{ marginLeft: '10px' }}>{ contact.phone }</small>
      <br />
      <small style={{ marginLeft: '10px' }}>{ contact.email }</small>
    </div>
  );
};
