import { boxes } from "./Boxes";
import React from 'react';

export default function Practice() {
  const [arrayOfBoxes, setArrayOfBoxes] = React.useState(boxes);
  const toggleBackgroundColor = () => setArrayOfBoxes(prev => ({ ...prev, on: !prev.on }));

  const jsxBoxes = arrayOfBoxes.map(box => <p key={box.id} style={{ border: '1px solid black', height: '100px', width: '100px', display: 'inline-block', margin: '5px', borderRadius: '5px', background: box.on ? 'black': 'white' }} onClick={ toggleBackgroundColor }></p>);

  return (
    <div>
        <p style={{ border: '1px solid black', height: '100px', width: '100px', display: 'inline-block', margin: '5px', borderRadius: '5px' }} onClick={ toggleBackgroundColor }></p>
       
    </div>
  );
};
