import { useState, useEffect } from "react";
import { Link, useSearchParams } from "react-router-dom";

export default function Cars() {
  const [searchParams, setSearchParams] = useSearchParams();
  const typeFilter = searchParams.get("type");
  const [cars, setCars] = useState([]);
  useEffect(() => {
    const getCars = async () => {
        try {
            const res = await fetch('/api/cars');
            const data = await res.json();
            const filteredCars = typeFilter ? data.cars.filter(car => car.type === typeFilter)
            : data.cars;
            setCars(filteredCars);
        } catch(err) {
            throw new Error('Something went wrong');
        }
    }
    getCars();
  }, [typeFilter]);

  localStorage.setItem("cars", JSON.stringify(cars));

  const carComponents = cars.map(car => {
    return (
        <Link key={car.id} to={ `${car.id}` } className="car-details-link">
            <img src={ car.imageUrl } alt='car avatar' className="car-photo"/>
            <div className="car-details">
                <h3 className="car-name">{ car.name }</h3>
                <p className="car-price">${ car.price }/day</p>
            </div>
            <div 
                className="car-type"
                style={{
                    background: car.type === 'classic' ?
                    'orange': car.type === 'top class' ?
                    'black': 'indigo'
                }}
            >
                { car.type }
            </div>
        </Link>
    );
  });
  console.log(cars);
 
  return (
    cars.length > 0 ? (
        <div className="car-container">
            <h1 className="car-h1">Explore our car options</h1>
            <Link to='?type=rugged'>rugged</Link>
            <Link to="?type=top class">top class</Link>
            <Link to='?type=classic'>classic</Link>
            <Link to='/'>all</Link>
            <div className="car-photo-container">
            { carComponents }
            </div>
       </div>
    ) : <p>Loading...</p>
  );
};
