import { memeApiObject } from "./memeData";

const arrayOfMemeObjects = memeApiObject.data.memes.map(meme => meme);
const size = arrayOfMemeObjects.length;
const url = arrayOfMemeObjects[Math.floor(Math.random() * size)].url;
function appendImage() {
  const body = document.querySelector('body');
  const image = <img src={ url } alt="react"/>
  body.appendChild(image);
};

export default function Meme() {
  return (
