import { boxes } from "./Boxes";
import Box from "./Box";
import React from 'react';

export default function Practice() {
  const [arrayOfBoxes, setArrayOfBoxes] = React.useState(boxes);

  const toggleBackgroundColor = () => setArrayOfBoxes(prev => ({ ...prev, on: !prev.on }));

  const jsxBoxes = arrayOfBoxes.map(box => <Box on={ box.on } handleClick={ toggleBackgroundColor }/>);

  return (
    <div>
        { jsxBoxes }
    </div>
  );
};
