import React from "react";
import { memeApiObject } from "./memeData";
const arrayOfMemeObjects = memeApiObject.data.memes.map(meme => meme);
const size = arrayOfMemeObjects.length;
let url;
console.log(url);
function AddImage() {
  const [picture, setPicture] = React.useState(arrayOfMemeObjects[Math.floor(Math.random() * size)].url);
  setPicture((prev) => arrayOfMemeObjects[Math.floor(Math.random() * size)].url);
  url = picture;
};

export default function Meme() {
  return (
